import static android.content.Context.MODE_PRIVATE;

/**
 * A simple {@link Fragment} subclass.
 */
public class Fragment_Home extends Fragment {


    public Fragment_Home() {
        // Required empty public constructor
    }
    String URL_PROFILE="data:image/png;base64,";
ImageView profile_photo;
ProgressBar imageProgress;
String BRANCH="CSE_2015_2019";
TextView appusername,sscMarks,interMarks;
TextView fys1,fys2,sys1,sys2,tys1;
RetroGet retroGet;
    String API_KEY="AKPhEaFsE8c1f98hiX1VXa0dj5_7KFq0";

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View v=inflater.inflate(R.layout.fragment_fragment__home, container, false);
        profile_photo=v.findViewById(R.id.profile_photo);
        imageProgress=v.findViewById(R.id.imageProgress);
        imageProgress.setIndeterminate(true);
        imageProgress.setVisibility(View.VISIBLE);
        appusername=v.findViewById(R.id.appusername);
        sscMarks=v.findViewById(R.id.SSCMARKS);
        interMarks=v.findViewById(R.id.INTERMARKS);
        fys1=v.findViewById(R.id.fysem1);
        fys2=v.findViewById(R.id.fysem2);
        sys1=v.findViewById(R.id.sysem1);
        sys2=v.findViewById(R.id.sysem2);
        tys1=v.findViewById(R.id.tysem1);
        SharedPreferences sharedPreferences=v.getContext().getSharedPreferences("MyLogin",MODE_PRIVATE);
       // editor.putString("username", user);
      //  editor.putString("password", passWord);
       // editor.putBoolean("logged", true);
        String UNAME=sharedPreferences.getString("username","");
        String PASS=sharedPreferences.getString("password","");
        Glide.with(v.getContext()).load(URL_PROFILE).apply(RequestOptions.circleCropTransform()).listener(new RequestListener<Drawable>() {
            @Override
            public boolean onLoadFailed(@Nullable GlideException e, Object model, Target<Drawable> target, boolean isFirstResource) {
                imageProgress.setVisibility(View.INVISIBLE);
                return false;
            }

            @Override
            public boolean onResourceReady(Drawable resource, Object model, Target<Drawable> target, DataSource dataSource, boolean isFirstResource) {
                imageProgress.setVisibility(View.INVISIBLE);
                return false;
            }
        }).into(profile_photo);
        retroGet= RetroServer.getRetrofit().create(RetroGet.class);
        String q="{\"field8\":{$eq:\""+UNAME+"\"},\"field9\":{$eq:\""+PASS+"\"}}";
        String f="{\"field10\": 1,\"field17\": 1,\"field18\": 1,\"field72\": 1,\"field109\": 1,\"field150\": 1,\"field188\": 1,\"field229\": 1}";

        Call<List<UserDetails>> dataCall = retroGet.getData(BRANCH,API_KEY,q,f);
        dataCall.enqueue(new Callback<List<UserDetails>>() {
            @Override
            public void onResponse(Call<List<UserDetails>> call, Response<List<UserDetails>> response) {
                appusername.setText(response.body().get(0).getName());
                sscMarks.setText(response.body().get(0).getSsc());
                interMarks.setText(response.body().get(0).getInter());
                assert response.body() != null;
                fys1.setText(response.body().get(0).getFys1());
                fys2.setText(response.body().get(0).getFys2());
                sys1.setText(response.body().get(0).getSys1());
                sys2.setText(response.body().get(0).getSys2());
                tys1.setText((response.body().get(0).getTys1()).substring(0,5));
            }

            @Override
            public void onFailure(Call<List<UserDetails>> call, Throwable t) {

            }
        });
        return v;
    }

}
